mapper.update:
UPDATE user_profile
    SET full_name = #{fullName},
        terms_of_agreement = #{termsOfAgreement}
    WHERE
        id = #{id};

mapper.insert:
INSERT INTO user_profile (
    user_id,
    full_name,
    terms_of_agreement
) VALUES (
    #{userId},
    #{fullName},
    #{termsOfAgreement}
);
